I did some manual inlining and simple optimizations (see code below). It usually runs 20-25% faster in Chrome / FF.

regards
david

```
var snip = function ( contour, u, v, w, n, verts ) {

    var p;
    var ax, ay, bx, by;
    var cx, cy, px, py;

    ax = contour[ verts[ u ] ].x;
    ay = contour[ verts[ u ] ].y;

    bx = contour[ verts[ v ] ].x;
    by = contour[ verts[ v ] ].y;

    cx = contour[ verts[ w ] ].x;
    cy = contour[ verts[ w ] ].y;

    var aX = cx - bx, aY = cy - by, bX = ax - cx, bY = ay - cy, cX = bx - ax, cY = by - ay;

    if ( EPSILON > ((cX*(cy-ay)) - (cY*(cx-ax))) ) return false;

    var apx, apy, bpx, bpy, cpx, cpy;

    for ( p = 0; p < n; p++ ) {

        if( (p !== u) && (p !== v) && (p !== w) ) {

            px = contour[ verts[ p ] ].x
            py = contour[ verts[ p ] ].y

            apx= px  -ax;  apy= py - ay;
            bpx= px - bx;  bpy= py - by;
            cpx= px - cx;  cpy= py - cy;

            if( ( (aX*bpy - aY*bpx) >= 0.0 ) && ( (cX*apy - cY*apx) >= 0.0 ) && ( (bX*cpy - bY*cpx) >= 0.0 ) )
                return false;
        }
    }

    return true;

};
