            if(My<vs[i].position.y)My=vs[i].position.y;
            if(Mz<vs[i].position.z)Mz=vs[i].position.z;
        }
        geometry = new THREE.Geometry();
        geometry.vertices = vs;
        geometry.faces = fs;
        if(mesh)
            scene.remove( mesh );
        mesh = new THREE.Mesh( geometry, material );
        scene.add( mesh );
        render();
    }

