THREE.MyTorusGeometry = function ( radius, tube, radialSegments, tubularSegments, arc ) {

  THREE.Geometry.call( this );

  var scope = this;

  this.radius = radius || 100;
  this.tube = tube || 40;
  this.radialSegments = radialSegments || 8;
  this.tubularSegments = tubularSegments || 6;
  this.arc = arc || Math.PI * 2;

  var u,v,
      vFactor = 1 / this.radialSegments * Math.PI * 2,
      uFactor = 1 / this.tubularSegments * this.arc,
      vXVertex,
      vYVertex,
      vZVertex,
      vCos, vSin, uCos, uSin,
      i, j,
      vertices = this.vertices;

  var uvs = [], normals = [];

  for ( j = 0; j <= this.radialSegments; j ++ ) {

    v = j * vFactor;

    vCos = Math.cos( v );
    vSin = Math.sin( v );

    vXVertex = ( this.radius + this.tube * vCos );
    vYVertex = ( this.radius + this.tube * vCos );
    vZVertex = this.tube * vSin;

    for ( i = 0; i <= this.tubularSegments; i ++ ) {

      u = i * uFactor;

      uCos = Math.cos( u );
      uSin = Math.sin( u );

      var vertex = new THREE.Vector3( vXVertex * uCos, vYVertex * uSin, vZVertex);
      vertices.push( vertex );

      uvs.push( new THREE.UV( i / this.tubularSegments, j / this.radialSegments ) );

      var normal = new THREE.Vector3( vertex.x - this.radius * uCos, vertex.y - this.radius * uSin, vZVertex) ;
      normals.push( normal.normalize() );
    }
  }

  var tubPlusOne = this.tubularSegments + 1,
      a, b, c, d,
      na, nb, nc, nd,
      va, vb, vc, vd,
      aj = 0, aj1 = 0,
      face, faceNormal, faceCentroid;

  for ( j = 1; j <= this.radialSegments; j ++ ) {

    aj = aj1 + tubPlusOne;

    a = aj;
    b = aj1;

    na = normals[ a ];
    nb = normals[ b ];

    va = vertices[ a ];
    vb = vertices[ b ];

    for ( i = 1; i <= this.tubularSegments; i ++ ) {

      //a = aj + i - 1;
      //b = aj1 + i - 1;

      c = aj1 + i;
      d = aj + i;

      nc = normals[ c ];
      nd = normals[ d ];

      face = new THREE.Face4( a, b, c, d, [ na, nb, nc, nd ] );
      faceNormal = face.normal;

      faceNormal.x = na.x + nb.x + nc.x + nd.x;
      faceNormal.y = na.y + nb.y + nc.y + nd.y;
      faceNormal.z = na.z + nb.z + nc.z + nd.z;

      faceNormal.normalize();

      faceCentroid = face.centroid;

      vc = vertices[ c ];
      vd = vertices[ d ];

      faceCentroid.x = ( va.x + vb.x + vc.x + vd.x ) * 0.25;
      faceCentroid.y = ( va.y + vb.y + vc.y + vd.y ) * 0.25;
      faceCentroid.z = ( va.z + vb.z + vc.z + vd.z ) * 0.25;

      this.faces.push( face );
      this.faceVertexUvs[ 0 ].push( [ uvs[ a ], uvs[ b ], uvs[ c ], uvs[ d ] ] );

      a = d;
      b = c;

      na = nd;
      nb = nc;

      va = vd;
      vb = vc;
    }

    aj1 += tubPlusOne;
  }
};

THREE.MyTorusGeometry.prototype = Object.create( THREE.Geometry.prototype );
