import 'package:flutter/rendering.dart';
import 'package:flutter/scheduler.dart';
import 'package:flutter_test/flutter_test.dart';

const cellHeight = 20.0;

void main() {
  Future<void> setUp(WidgetTester tester) async {
    tester.binding.window.physicalSizeTestValue = Size(200, 200);
    tester.binding.window.devicePixelRatioTestValue = 1.0;

    await tester.pumpWidget(
      MaterialApp(
        home: Material(
          child: CustomScrollView(
            slivers: <Widget>[
              SliverList(
                delegate: SliverChildListDelegate(
                  List.generate(
                    200,
                    (i) => ConstrainedBox(
                          constraints:
                              BoxConstraints.tightFor(height: cellHeight),
                          child: Text('Item $i'),
                        ),
                  ),
                ),
              )
            ],
          ),
        ),
      ),
    );
  }

  group('RenderViewport with AxisDirection.down', () {
    testWidgets('Scroll Direction for RenderViewport',
        (WidgetTester tester) async {
      await setUp(tester);

      RenderViewport renderViewport =
          tester.element(find.byType(Viewport)).renderObject;
      ScrollDirection scrollDirection;

      renderViewport.offset.addListener(
          () => scrollDirection = renderViewport.offset.userScrollDirection);

      expect(renderViewport.axisDirection, AxisDirection.down);

      expect(find.text('Item 0'), findsOneWidget);
      expect(find.text('Item 9'), findsOneWidget);
      expect(find.text('Item 10'), findsNothing);

      await tester.drag(find.byType(CustomScrollView), Offset(0, -cellHeight));
      await tester.pump();

      expect(find.text('Item 0'), findsNothing);
      expect(find.text('Item 9'), findsOneWidget);
      expect(find.text('Item 10'), findsOneWidget);

      // FAILS: Actual value ScrollDirection.reverse
      expect(scrollDirection, ScrollDirection.forward);
    });

    testWidgets('Scroll Offset for RenderViewport',
        (WidgetTester tester) async {
      await setUp(tester);

      RenderViewport renderViewport =
          tester.element(find.byType(Viewport)).renderObject;

      final initialPixelOffset = renderViewport.offset.pixels;

      expect(renderViewport.axisDirection, AxisDirection.down);

      expect(find.text('Item 0'), findsOneWidget);
      expect(find.text('Item 9'), findsOneWidget);
      expect(find.text('Item 10'), findsNothing);

      await tester.drag(find.byType(CustomScrollView), Offset(0, -cellHeight));
      await tester.pump();

      expect(find.text('Item 0'), findsNothing);
      expect(find.text('Item 9'), findsOneWidget);
      expect(find.text('Item 10'), findsOneWidget);

      final finalPixelOffset = renderViewport.offset.pixels;

      // PASSES
      expect(finalPixelOffset, greaterThan(initialPixelOffset));
    });

    testWidgets('Scroll Direction for GrowthDirection forward RenderSliverList',
        (WidgetTester tester) async {
      await setUp(tester);

      ScrollDirection scrollDirection;
      RenderSliverList renderSliverList =
          tester.element(find.byType(SliverList)).renderObject;
      RenderViewport renderViewport =
          tester.element(find.byType(Viewport)).renderObject;
      renderViewport.offset.addListener(() => SchedulerBinding.instance
          .addPostFrameCallback((_) => scrollDirection =
              renderSliverList.constraints.userScrollDirection));

      expect(renderSliverList.constraints.axisDirection, AxisDirection.down);
      expect(renderSliverList.constraints.growthDirection,
          GrowthDirection.forward);

      expect(find.text('Item 0'), findsOneWidget);
      expect(find.text('Item 9'), findsOneWidget);
