	| 0 -> (eval.(i) <- (eval.(i) + 0)) (* 0 *)
	| 1 -> (eval.(i) <- (eval.(i) + 1)) (* cp*)
	| 2 -> (eval.(i) <- (eval.(i) - 5)) (*out*)
	| 3 -> (eval.(i) <- (eval.(i) + 0)) (*-cp*)
	| _ -> () )
      done;
      (* deux voisins *)
      for v=0 to 5 do
	(match ((i/(puissance 4 v)) mod 4) with
	| 1 ->
	    if ((i/(puissance 4 ((v+1) mod 6))) mod 4)=1 then (eval.(i) <- (eval.(i) + 1)) else (); (* ami ortho *)
	    if ((i/(puissance 4 ((v+2) mod 6))) mod 4)=1 then (eval.(i) <- (eval.(i) + 1)) else (); (* ami meta  *)
	    if ((i/(puissance 4 ((v+3) mod 6))) mod 4)=1 then (eval.(i) <- (eval.(i) + 4)) else (); (* ami para  *)
	| 3 ->
