(* coups en avant *)
let coup_avant code =
  let v = code / 10000
  and x = code / 1000 - 10 * (code / 10000)
  and y = code / 100 - 10 * (code / 1000)
  and (dx,dy) = dir_tab.(code / 10 - 10 * (code / 100))
  and l = code - 10 * (code /10) in
  if v<2 then (* coup en ligne *)
    begin
      if v=1 then
	if tabl.(x).(y)=1
	then incr out_m
	else incr out_p
      else ();
      for i=(v+1) to l do
	tabl.(x + (l+1-i)*dx).(y + (l+1-i)*dy) <- tabl.(x + (l-i)*dx).(y + (l-i)*dy)
      done;
      tabl.(x).(y) <- 0
    end
  else (* coup en fleche *)
    begin
      let (vx,vy) = dir_tab.(v) in
      for i=0 to l-1 do
	tabl.(x + i*dx + vx).(y + i*dy + vy) <- tabl.(x + i*dx).(y + i*dy);
	tabl.(x + i*dx).(y + i*dy) <- 0
      done;
    end;;

(* coups en arriere *)
let coup_arriere code =
  let v = code / 10000
  and x = code / 1000 - 10 * (code / 10000)
  and y = code / 100 - 10 * (code / 1000)
  and (dx,dy) = dir_tab.(code / 10 - 10 * (code / 100))
  and l = code - 10 * (code /10) in
  if v<2 then (* coup en ligne *)
    begin
      for i=0 to l-1-v do
	tabl.(x + i*dx).(y + i*dy) <- tabl.(x + (i+1)*dx).(y + (i+1)*dy)
      done;
      if v=0 then
	tabl.(x + l*dx).(y + l*dy) <- 0
      else
	begin
	  tabl.(x + (l-1)*dx).(y + (l-1)*dy) <- (-tabl.(x).(y));
	  if tabl.(x).(y)=1
	  then decr out_m
	  else decr out_p
	end
    end
  else (* coup en fleche *)
    begin
      let (vx,vy) = dir_tab.(v) in
      for i=0 to l-1 do
	tabl.(x + i*dx).(y + i*dy) <- tabl.(x + i*dx + vx).(y + i*dy + vy);
	tabl.(x + i*dx + vx).(y + i*dy + vy) <- 0
      done;
    end;;

(* generateur de coups *)
let gen cp =
  let list = ref []
  and cont = ref true
  and x = ref 0
  and y = ref 0
  and bb = ref 0
  and mb = ref 0 in
  for i=1 to 9 do
    for j=max 1 (i-4) to min 9 (i+4) do
      if tabl.(i).(j)=cp then
	begin
	  for d=1 to 6 do
	    let (dx,dy) = dir_tab.(d) in
	    cont := true;
	    x := i;
	    y := j;
	    bb := 1;
	    mb := 0;
	    while !cont do
	      x := !x + dx;
	      y := !y + dy;
	      match tabl.(!x).(!y)*cp with
	      |	1 ->
		  incr bb;
		  if (!bb > 3 or !mb > 0)
		  then cont := false
		  else 
		    begin (* coups en fleche *)
		      let (vx1,vy1) = dir_tab.(d+1) in
		      let (vx2,vy2) = dir_tab.(d+2) in
		      if (tabl.(i+vx1).(j+vy1)=0 && tabl.(i+dx+vx1).(j+dy+vy1)=0 && tabl.(!x+vx1).(!y+vy1)=0)
		      then (list := (((d+1)*10000 + i*1000 + j*100 + d*10 + !bb) :: !list))
		      else ();
		      if (tabl.(i+vx2).(j+vy2)=0 && tabl.(i+dx+vx2).(j+dy+vy2)=0 && tabl.(!x+vx2).(!y+vy2)=0)
		      then (list := (((d+2)*10000 + i*1000 + j*100 + d*10 + !bb) :: !list))
		      else ()
		    end
	      |	-1 ->
		  incr mb;
		  if (!mb = !bb)
		  then cont := false
		  else ()
	      |	0 ->         (* coups en ligne sans ejection *)
		  list := ((i*1000 + j*100 + d*10 + !bb + !mb) :: !list);
		  cont := false
	      |	_ ->
		  if (!mb>0) (* coups en ligne avec ejection *)
		  then list := ((10000 + i*1000 + j*100 + d*10 + !bb + !mb) :: !list)
		  else ();
		  cont := false
	    done
	  done
	end
    done
  done;
  !list;;

