let comp coup_1 coup_2 =
  coup_avant coup_1;
  let e1 = evaluate () in
  coup_arriere coup_1;
  coup_avant coup_2;
  let e2 = evaluate () in
  coup_arriere coup_2;
  e1-e2;;

let rec ab p alpha beta cp =
  let a = ref alpha
  and b = ref beta in
  let l = ref (gen cp) in
  if (p=1) then
    begin
      if (cp>0) then
	begin
	  while ((!a)<(!b) && (!l<>[])) do
	    coup_avant (List.hd !l);
	    a := max !a (evaluate ());
	    coup_arriere (List.hd !l);
	    l := List.tl !l
	  done;
	  !a
	end
      else
	begin
	  while ((!a)<(!b) && (!l<>[])) do
	    coup_avant (List.hd !l);
	    b := min !b (evaluate ());
	    coup_arriere (List.hd !l);
	    l := List.tl !l
	  done;
	  !b
	end
    end
  else
    begin
      if (cp>0) then
	begin
	  while ((!a)<(!b) && (!l<>[])) do
	    coup_avant (List.hd !l);
	    a := max !a (ab (p-1) !a !b (-cp));
	    coup_arriere (List.hd !l);
	    l := List.tl !l
	  done;
	  !a
	end
      else
	begin
	  while ((!a)<(!b) && (!l<>[])) do
	    coup_avant (List.hd !l);
	    b := min !b (ab (p-1) !a !b (-cp));
	    coup_arriere (List.hd !l);
	    l := List.tl !l
	  done;
	  !b
	end
    end;; 

let ab1 p0 cp =
  let a = ref (-inf)
  and b = ref (inf)
  and c = ref 0 
  and e = ref 0 in
