  "info": {
    "title": "Example API",
    "description": "API description",
    "version": "1.0.1"
  },
  "schemes": [
    "https"
  ],
  "produces": [
    "application/json; charset  = utf8"
  ],
  "paths": {
    "/withoutOverwriting": {
      "post": {
        "description": "withoutOverwriting description",
        "parameters": [
          {
            "name": "body",
            "in": "body",
            "description": "parameters description",
            "required": true,
            "schema": {
              "$ref": "#/definitions/EntityWithoutOverwriting"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success response of the request"
          }
        }
      }
    },
    "/withOverwriting": {
      "post": {
        "description": "withOverwriting description",
        "parameters": [
          {
            "name": "body",
            "in": "body",
            "description": "parameters description",
            "required": true,
            "schema": {
              "$ref": "#/definitions/EntityWithOverwriting"
